<template>
  <div class="sidebar">
    <div class="sidebar-logo__wrapper">
      <img src="data:image/png;base64," alt="logo" class="sidebar-img"/>
      <!-- <h1 class="sidebar-logo__title">{{ shop.shopname }}</h1> -->
    </div>
    <ul class="left-nav">
      <li v-for="nav in navs">
        <router-link class="sidebar-nav__item" v-if="nav.pathname" :to="router('main/' + nav.pathname)">{{ nav.val }}</router-link>
        <a v-else class="sidebar-nav__item" @click="toggle(nav.subnav)" :class="{'dark': $route.fullPath.indexOf(nav.subnav) > -1}">
          {{ nav.val }}
          <i v-if="nav.sub" class="icon-down_arrow" :class="{'icon-down_arrow__rotate': !isDark[nav.subnav]}"></i>
        </a>
        <transition name="collpase">
          <ul v-if="nav.sub" v-show="isDark[nav.subnav]" class="collpase" :class="{'dark': $route.fullPath.indexOf(nav.subnav) > -1}">
            <li v-for="ls in nav.sub">
              <router-link class="sidebar-nav__item sidebar-nav__subitem" :to="router('main/' + ls.pathname)">
                {{ ls.val }}
              </router-link>
            </li>
          </ul>
        </transition>
      </li>
    </ul>
    <div class="copyright_wrapper" v-if="role.haiwai">
    <el-select v-model="lang"  icon="caret-bottom" @change="switchLanguage" size="small" popperClass="popperBg" style="width:80%;">
        <el-option v-for="item in langLists" :key="item.value" :label="item.label" :value="item.value"></el-option>
      </el-select>
    </div>
  </div>
</template>
<script>
  import Store from '../../common/js/store';
  import config from 'src/config';
  export default {
    data() {
      return {
        lang: config.lang,
        role: Store.get('role') || {},
        navs: [],
        isDark: {
          trans: true,
          Setting: true
        },
        langLists: [
          {label: '日本語', value: 'ja'},
          {label: 'English', value: 'en'},
          {label: '繁體中文', value: 'zh-TW'},
          {label: '简体中文', value: 'zh-CN'}
          ]
      };
    },

    props: {
      shop: {
        type: Object
      }
    },

    created() {
      this.getNav();
    },

    methods: {
      switchLanguage(value, label) {
        localStorage.setItem("lang", JSON.stringify({label: label, value: value}));
        this.role[value] = true;
        window.location.reload()
      },
      router(router) {
        return `/${router}`;
      },

      toggle(val) {
        this.isDark[val] = !this.isDark[val];
      },

      getNav() {
        switch (this.role.country) {
          case 'HK':
            if(this.role.single) { // 香港单店,无收银员管理
              if(this.role.isCashier) { // 是收营员
                this.navs = [
                  {
                    val: this.$t('nav.tradeMng'), // 交易管理
                    pathname: 'transctl'
                  },
                  {
                    val: this.$t('nav.shopMng'), // 账户信息
                    pathname: 'singlemanage'
                  },
                  {
                    val: this.$t('nav.contact'), // 客服
                    pathname: 'contact'
                  }
                ];
              } else { // 不是收营员
                this.navs = [ // 单店
                  {
                    val: this.$t('nav.index'), // 首页
                    pathname: 'index'
                  },
                  {
                    val: this.$t('nav.tradeMng'), // 交易管理
                    pathname: 'transctl'
                  },
                  {
                    val: this.$t('nav.shopMng'), // 账户信息
                    pathname: 'singlemanage'
                  },
                  {
                    val: this.$t('nav.emailSet'), // 邮箱设置
                    pathname: 'emailSetting'
                  },
                  {
                    val: this.$t('nav.contact'), // 客服
                    pathname: 'contact'
                  }
                ];
              }
            } else { // 香港大商户
              this.navs = [
                {
                  val: this.$t('nav.index'), // 首页
                  pathname: 'index'
                },
                {
                  val: this.$t('nav.tradeMng'), // 交易管理
                  subnav: 'trans',
                  sub: [{
                    val: this.$t('nav.tradeAssign'), // 交易流水
                    pathname: 'transctl'
                  }, {
                    val: this.$t('nav.refundCheck'), // 退款审核
                    pathname: 'transCheck'
                  }]
                },
                {
                  val: this.$t('nav.shopMng'), // 账户信息
                  pathname: 'chainmanage'
                },
                {
                  val: this.$t('nav.setup'), // 设置
                  subnav: 'Setting',
                  sub: [{
                      val: this.$t('nav.passSet'), // 管理密码设置
                      pathname: 'passSetting'
                    }, {
                    val: this.$t('nav.emailSet'), // 邮箱设置
                    pathname: 'emailSetting'
                  }]
                },
                {
                  val: this.$t('nav.contact'), // 客服
                  pathname: 'contact'
                }
              ];
            }
            break;
          case 'AR':
          case 'JP':
            if(this.role.single) { // 迪拜单店
              if(this.role.isCashier) { // 是收营员
                this.navs = [
                  {
                    val: this.$t('nav.tradeMng'), // 交易管理
                    pathname: 'transctl'
                  },
                  {
                    val: this.$t('nav.shopMng'), // 账户信息
                    pathname: 'singlemanage'
                  },
                  {
                    val: this.$t('nav.contact'), // 客服
                    pathname: 'contact'
                  }
                ];
              } else { // 不是收营员
                this.navs = [ // 单店
                  {
                    val: this.$t('nav.index'), // 首页
                    pathname: 'index'
                  },
                  {
                    val: this.$t('nav.tradeMng'), // 交易管理
                    pathname: 'transctl'
                  },
                  {
                    val: this.$t('nav.shopMng'), // 账户信息
                    pathname: 'singlemanage'
                  },
                  {
                    val: this.$t('nav.cashMng'), // 收银员管理
                    pathname: 'cashiermanage'
                  },
                  {
                    val: this.$t('nav.emailSet'), // 邮箱设置
                    pathname: 'emailSetting'
                  },
                  {
                    val: this.$t('nav.contact'), // 客服
                    pathname: 'contact'
                  }
                ];
              }
            } else { // 迪拜大商户
              this.navs = [
                {
                  val: this.$t('nav.index'), // 首页
                  pathname: 'index'
                },
                {
                  val: this.$t('nav.tradeMng'), // 交易管理
                  subnav: 'trans',
                  sub: [{
                    val: this.$t('nav.tradeAssign'), // 交易流水
                    pathname: 'transctl'
                  }, {
                    val: this.$t('nav.refundCheck'), // 退款审核
                    pathname: 'transCheck'
                  }]
                },
                {
                  val: this.$t('nav.shopMng'), // 账户信息
                  pathname: 'chainmanage'
                },
                {
                  val: this.$t('nav.setup'), // 设置
                  subnav: 'Setting',
                  sub: [{
                    val: this.$t('nav.passSet'), // 管理密码设置
                    pathname: 'passSetting'
                  }, {
                    val: this.$t('nav.emailSet'), // 邮箱设置
                    pathname: 'emailSetting'
                  }]
                },
                {
                  val: this.$t('nav.contact'), // 客服
                  pathname: 'contact'
                }
              ];
            }
            break;
          default:
            if(this.role.single) { // 其他国家单店
              if(this.role.isCashier) { // 收银员
                this.navs = [
                  {
                    val: this.$t('nav.tradeMng'), // 交易管理
                    pathname: 'transctl'
                  },
                  {
                    val: this.$t('nav.shopMng'), // 账户信息
                    pathname: 'singlemanage'
                  }
                ];
              }else {
                this.navs = [ // 单店
                  {
                    val: this.$t('nav.index'), // 首页
                    pathname: 'index'
                  },
                  {
                    val: this.$t('nav.tradeMng'), // 交易管理
                    pathname: 'transctl'
                  },
                  {
                    val: this.$t('nav.shopMng'), // 账户信息
                    pathname: 'singlemanage'
                  },
                  {
                    val: this.$t('nav.cashMng'), // 收银员管理
                    pathname: 'cashiermanage'
                  },
                  {
                    val: this.$t('nav.emailSet'), // 邮箱设置
                    pathname: 'emailSetting'
                  }
                ];
              }
            } else { // 大商户
              this.navs = [
                {
                  val: this.$t('nav.index'), // 首页
                  pathname: 'index'
                },
                {
                  val: this.$t('nav.tradeMng'), // 交易管理
                  subnav: 'trans',
                  sub: [{
                    val: this.$t('nav.tradeAssign'), // 交易流水
                    pathname: 'transctl'
                  }, {
                    val: this.$t('nav.refundCheck'), // 退款审核
                    pathname: 'transCheck'
                  }]
                },
                {
                  val: this.$t('nav.shopMng'), // 账户信息
                  pathname: 'chainmanage'
                },
                {
                  val: this.$t('nav.setup'), // 设置
                  subnav: 'Setting',
                  sub: [{
                    val: this.$t('nav.passSet'), // 管理密码设置
                    pathname: 'passSetting'
                  }, {
                    val: this.$t('nav.emailSet'), // 邮箱设置
                    pathname: 'emailSetting'
                  }]
                }
              ];
            }
        }

        // 海外
        if(this.role.haiwai) {
          // 大商户，直营增加清算查询模块,退款审核
          if (this.role.isMerchant && !this.role.isCashier) {
            this.navs.splice(1, 0, {
              val: this.$t('nav.settlement'), // 清算查询
              pathname: 'settlement'
            });

            this.navs.splice(2, 1, {
              val: this.$t('nav.tradeMng'), // 交易管理
              subnav: 'trans',
              sub: [{
                val: this.$t('nav.tradeAssign'), // 交易流水
                pathname: 'transctl'
              }, {
                val: this.$t('nav.refundCheck'), // 退款审核
                pathname: 'transCheck'
              }]
            });
          }
        }

      }
    }
  };
</script>
<style lang="scss">

  .sidebar {
    overflow-y: scroll;
    position: fixed;
    width: 220px;
    height: 100%;
    background-color: #2A2A2A;
    @at-root .sidebar-logo__wrapper {
      margin: 20px auto 28px;
      text-align: center;
    }
    @at-root .sidebar-img {
      margin-bottom: 13px;
    }
    @at-root .sidebar-logo__title {
      font-size: 20px;
      color: #fff;
      word-break: break-all;
      word-wrap: break-word;
      line-height: 1.4;
      padding: 0 27px;
    }
    .sidebar-nav__item {
      position: relative;
      display: block;
      line-height: 48px;
      padding-left: 27px;
      cursor: pointer;
      &.sidebar-nav__subitem {
        position: relative;
        padding-left: 45px;
        overflow: hidden;
        &::after {
          content: '';
          position: absolute;
          width: 100%;
          height: 1px;
          top: 0px;
          left: 27px;
          background-color: #373737;
        }
      }
      &.active {
        background-color: #000;
        &::before {
          content: '';
          position: absolute;
          left: 0;
          top: 0;
          width: 7px;
          height: 100%;
          background-color: #FE9B20;
        }
      }
      .icon-down_arrow {
        position: absolute;
        font-size: 14px;
        right: 16px;
        top: 17px;
        transform-origin: center center;
        transition: .3s cubic-bezier(.33,.48,.74,.86);
      }
    }
    .left-nav {
      padding-bottom: 50px;
    }
  }
  .icon-down_arrow__rotate {
    transform: rotateZ(-90deg);
  }
  .dark {
    background-color: #000;
  }

  .copyright_wrapper {
    position: fixed;
    left: 6px;
    bottom: 10px;
    text-align: center;
    font-size: 14px;
    @at-root .copyright-text {
      color: #fff;
    }
    @at-root .copyright-desc {
      text-align: center;
      color: #FE9B20;
    }
    .custom-stylee {
      background-color: #2a2a2a;
      color: #8a8c92;
    }
  }
  .collpase {
    overflow: hidden;
  }
  .collpase-enter-active {
    animation: expand-enter .3s cubic-bezier(.33,.48,.74,.86);
  }
  .collpase-leave-active {
    animation: expand-leave .3s cubic-bezier(.33,.48,.74,.86);
  }

  @keyframes expand-enter {
    0% {
      height: 0px;
    }

    100% {
      height: 144px;
    }
  }
  @keyframes expand-leave {
    0% {
      height: 144px;
    }

    100% {
      height: 0px;
    }
  }
  .popperBg {
    background-color: white;
  }
</style>
